import React, {useState} from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from './ExpenseFilter';

const Expenses = (props) => {
  const [year, setyear] = useState('2020')
  const newYearChanged = (year) =>{
    setyear(year);
  }
  const filteredarray = props.items.filter(exp => {
    return exp.date.getFullYear().toString() === year;
  });
  return (
    <div>

    <Card className="expenses">
     <ExpensesFilter selected = {year} onNewYear ={newYearChanged}></ExpensesFilter>
     {
      filteredarray.length === 0 ? <p>No expenses found</p> :   filteredarray.map(expense=> 
      <ExpenseItem 
      key ={expense.id}
      title={expense.title}
      amount ={expense.amount}
      date ={expense.date}

      />)}

    </Card>
    </div>
  );
}

export default Expenses;
