import "./Savingsitem.css";

import SavingsDate from "./SavingsDate";

function SavingsItem(props) {
  return (
    <div className="savings-item">
      <SavingsDate date={props.date} />
      <div className="savings-item__description">
        <h2>{props.title}</h2>
        <div className="savings-item__price">{props.amount}</div>
      </div>
    </div>
  );
}
export default SavingsItem;
